package pack1;

public class Identfier {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=4;
		int n2=2;
		int s,diff,p,q,rem,intrest;
		s=n1+n2;
		System.out.println("value of the sum " +n1+" and "+n2+" is "+s);
		diff=n1-n2;
		System.out.println("value of the differ "+n1+" and "+n2+" is "+diff);
		p=n1*n2;
		System.out.println("value of the product "+n1+" and "+n2+" is "+p);
		q=n1/n2;
		System.out.println("value of the q "+n1+" and "+n2+" is "+q);
		rem=n1%n2;
		System.out.println("value of rem is "+n1+" and "+n2+" is "+rem);
		intrest=n1&n2;
		System.out.println("value of the intrest "+n1+" and "+n2+" is "+intrest);
	}

}
